import React from "react";

import { getFunName } from "./../helpers";

class StorePicker extends React.Component {
  myInput = React.createRef();
    
  goToStore = (event) => {
    event.preventDefault();
    // get test from form
    // two wasy to handle input
    const storeName= this.myInput.value;
    console.log("SOTENAME: ", storeName);
    // change page to .../store/WHAT-EVER
    this.props.history.push(`/store/${this.storeName}`);
    
  }
  
  render() {
    return (
      <form className="store-selector" onSubmit={this.goToStore}>
	<h2> Please Enter a Store</h2>
	<input type="text"
	       ref={this.myInput}
	       required
	       placeholder="Store Name"
	       defaultValue={getFunName()}
	/>
	<button type="submit">Vist Store â†’</button>
      </form>
    );
  };
};


export default StorePicker;
